const initialState = {
  todoList: {},
  todoListLength: 0,
  showTodoForm: false
};

const ADD_TODO_ITEM = '/add';
const EDIT_TODO_ITEM = '/edit';

// Add a new todo item
export function addTodoItem(newTodoTask, newTodoDesc) {
    const newTodoItem = {
      task: newTodoTask,
      desc: newTodoDesc,
      status: false,
    };

    return {
      type: ADD_TODO_ITEM,
      payload: newTodoItem,
    };
};

// Edit any todo item: task & description
export function editTodoItem({ id, task, desc }){
  return{
    action: EDIT_TODO_ITEM,
    payload: {id, task, desc}
  }
};

