import React from 'react';
import { useDispatch } from 'react-redux';

import { toggleTodoForm } from '../stores/todo';

export default function TodoFormButton() {
  const dispatch = useDispatch();


  const handleTodoForm = (act) => dispatch(toggleTodoForm(act));

  return (
    <div className="create-todo">

      <button
        type="button"
        onClick={() => { handleTodoForm(true); }}
      >
        Create a todo.
      </button>
    </div>
  );
}
