import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import { useTodoListContext } from '../context/TodoListContext';
import { editTodoItem } from '../stores/todo';

import '../styles/TodoForm.scss';
import { EDIT_TODO_PLACEHOLDER } from '../utils/constants';

export default function EditTodo({ editableTodo, setEditView }) {
  const dispatch = useDispatch();
  const { id: oldTodoId, task: oldTodoTask, desc: oldTodoDesc } = editableTodo;

   // Updating our new todo, with the old todo's task
   const [newTodo, setNewTodo] = useState({
    id: oldTodoId,
    task: oldTodoTask,
    desc: oldTodoDesc,
  });

  // Update Todo list according to the updated todo-task
  const updateTodoItem = () => {

    dispatch(editTodoItem(newTodo));
    setEditView(false);
  };

  return (
    <div>
      <div className="todo-form__fields">
        <input
          type="text"
          className="todo-form__input"
          placeholder={EDIT_TODO_PLACEHOLDER.task}
          onChange={(e) => setNewTodo({ ...newTodo, task: e.target.value })}
          value={newTodo.task}
        />

        <textarea
          type="text"
          className="todo-form__input todo-form__desc"
          placeholder={EDIT_TODO_PLACEHOLDER.desc}
          onChange={(e) => setNewTodo({ ...newTodo, desc: e.target.value })}
          value={newTodo.desc}
        />
      </div>

      <button
        type="button"
        id="todo-submit-button"
        className="todo-form__button btn btn-primary"
        onClick={() => updateTodoItem()}
      >
        Update!
      </button>
    </div>
  );
}
