import React from 'react';

import { editTodoItem2, toggleTodoForm2 } from '../stores/todo';

export default function TodoFormButton() {
  const toggleTodoForm = (act) => {toggleTodoForm2(act);
  editTodoItem2();}
  return (
    <div className="create-todo">

      <button
        type="button"
        onClick={() => { toggleTodoForm(true); }}
      >
        Create a todo.
      </button>
    </div>
  );
}
