const initialState = {
  todoList: {},
  todoListLength: 0
};

const ADD_TODO_ITEM = '/todo';

// Add a new todo item
export function addTodoItem({ task: newTodoTask, desc: newTodoDesc }){
  const todoId = initialState.todoListLength
    ? initialState.todoListLength + 1
    : 0;

  const newTodoItem = {
    id: todoId,
    task: newTodoTask,
    desc: newTodoDesc,
    status: false,
  };

  return{
    action: ADD_TODO_ITEM,
    payload: newTodoItem
  }
};

// Edit any todo item: task & description
export function editTodoItem({ id: todoId, task: todoTask, desc: todoDesc }){
  return{
    action: EDIT_TODO_ITEM,
    payload: {todoId, todoTask, todoDesc}
  }
};

// Change status of todo item
const updateTodoItemStatus = (todoId) => {
  setTodoList({
    ...todoList,
    [todoId]: { ...todoList[todoId], status: !todoList[todoId].status },
  });
};

// Delete a delete todo item
const deleteTodoItem = (todoId) => {
  const tmpTodoList = { ...todoList };
  delete tmpTodoList[todoId];

  setTodoList(tmpTodoList);
};

export default function todoStore(state = initialState, action) {
    switch (action.type) {
      case ADD_TODO_ITEM:
        return {
          ...state,
          todoList: {...state.todoList, 1: action.payload},
        };
      default:
        return state;
    }
  }
