import React, { useState } from 'react';
import { useDispatch } from 'react-redux';

import { TODO_FORM_PLACEHOLDER } from '../utils/constants';
import { addTodoItem, toggleTodoForm } from '../stores/todo';
import '../styles/TodoForm.scss';

export default function TodoForm() {
  const dispatch = useDispatch();

  const [newTodo, setNewTodo] = useState({ task: '', desc: '' });

  const addTodo = () => {
    dispatch(addTodoItem(newTodo));
    dispatch(toggleTodoForm(false));
  };

  return (
    <div className="todo-form">
      <div className="todo-form__fields">
        <input
          type="text"
          className="todo-form__input"
          placeholder={TODO_FORM_PLACEHOLDER.task}
          value={newTodo.task}
          onChange={(e) => setNewTodo({ ...newTodo, task: e.target.value })}
        />

        <textarea
          type="text"
          className="todo-form__input todo-form__desc"
          placeholder={TODO_FORM_PLACEHOLDER.desc}
          value={newTodo.desc}
          onChange={(e) => setNewTodo({ ...newTodo, desc: e.target.value })}
        />
      </div>

      <button
        type="submit"
        id="todo-submit-button"
        className="todo-form__button btn btn-primary"
        onClick={addTodo}
      >
        Add todo!
      </button>
    </div>
  );
}
