const initialState = {
  todoList: {},
  todoListLength: 0,
  showTodoForm: false
};

const ADD_TODO_ITEM = '/add';
const EDIT_TODO_ITEM = '/edit';
const TOGGLE_TODO_FORM = '/toggle-form';
const UPDATE_TODO_ITEM_STATUS = '/update/status';
const DELETE_TODO_ITEM = '/delete'


// Add a new todo item
export function addTodoItem({task: newTodoTask, desc: newTodoDesc}) {
    const newTodoItem = {
      task: newTodoTask,
      desc: newTodoDesc,
      status: false,
    };

    return {
      type: ADD_TODO_ITEM,
      payload: newTodoItem,
    };
};

// Edit any todo item: task & description
export function editTodoItem({ oldTodoId, newTodoTask, newTodoDesc }){
  return{
    type: EDIT_TODO_ITEM,
    payload: { oldTodoId, newTodoTask, newTodoDesc }
  }
};

export function toggleTodoForm(act){
  return{
    type: TOGGLE_TODO_FORM,
    payload: act
  };
};

export function updateTodoItemStatus(todoId) {
  console.log("In funtion", todoId)
  return {
    type: UPDATE_TODO_ITEM_STATUS,
    payload: todoId,
  };
};

// Delete a delete todo item
export function delelteTodoItem (todoId){
  return{
    type: DELETE_TODO_ITEM,
    payload: todoId
  }
};

  export default function todoStore(state = initialState, action) {
    switch (action.type) {
      case ADD_TODO_ITEM:
        return {
          ...state,
          todoList: {...state.todoList, [state.todoListLength + 1]: {...action.payload, id: state.todoListLength + 1}},
          todoListLength: state.todoListLength + 1
        };
      case EDIT_TODO_ITEM:
        return {
          ...state,
          todoList: {...state.todoList, [action.payload.id]: {task: action.payload.task, desc: action.payload.desc}}
        };
      case TOGGLE_TODO_FORM:
        return{
          ...state,
          showTodoForm: action.payload
        };
      case UPDATE_TODO_ITEM_STATUS:
        return{
          ...state,
          todoList: {...state.todoList, [action.payload]: {...state.todoList[action.payload], status: !state.todoList[action.payload].status}}
        };
      case DELETE_TODO_ITEM:
        const tmpTodoList = { ...state.todoList };
        delete tmpTodoList[action.payload];

        return{
          ...state,
          todoList: tmpTodoList
        }
      default:
        console.log(action.payload, state)
        return state;
    }
  }
